import Menu from "../Menu";
import React, { useState, useEffect } from "react";
import { Container, Conteudo } from "../../styles/global";
import BuscarDadosApi from "../../paginas/Home/util";

export default function Funcionarios() {
  const funcionarios = BuscarDadosApi('/funcionario/listar');
  const [idFuncionario, setIdFuncionario] = useState('');
  let listarFuncionarios: any = [];
  localStorage.setItem('idFuncionario', idFuncionario);

  let img = <img width="20px" className="icon" src="/icons/lapis.png" alt="editar" />

  funcionarios.forEach((element: any) => {
    listarFuncionarios.push(
      <form action="">

        <input
          value={element.nome_usuario}
          name='nome'
          type="text"
        />
        <input
          className="none"
          value={element.id_funcionario}
          name='id_funcionario'
          type="text"
          onChange={e => setIdFuncionario(e.target.value)}
        />
        <input
          type="radio"
          value={element.id_funcionario}
          name="op"
          id={element.id_funcionario}
          onChange={e => setIdFuncionario({ valor: e.target.value, operacao: 'editar' })}
        />
        <input
          type="radio"
          value={element.id_funcionario}
          name="op"
          id={element.id_funcionario + "excluir"}
          onChange={e => setIdFuncionario({ valor: '1', operacao: 'excluir' })}
        />
        <button>
          <label htmlFor={element.id_funcionario}>{img}</label>
          <label htmlFor={element.id_funcionario + "excluir"}>x</label>
        </button>
      </form>
    )
  });

  return (
    <Container>
      <Menu></Menu>
      <Conteudo>
        funcionario selecionado: {localStorage.getItem('idFuncionario')}
        <div className="editar-excluir" >
          {listarFuncionarios}
        </div>
      </Conteudo>
    </Container>
  );
}
