import React, { useState, useEffect } from "react";
import Menu from "../Menu";
import api from "../../services/api";

import { Conteudo, Container, AgendarHorario, CompartilheLink, HorariosMarcados, CardFuncionario, CardAdicionarFuncionario } from "./styles";

export default class Home extends React.Component {
  constructor(props: any) {
    super(props);
    this.state = { value: '' };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);


  }

  funcionarios() {
    this.state = {
      funcionarios: []
    };
    let token = "2|LR3xXV504AqiQpFOShtJzclb3bWJVUOWpSRNmRbq";
    const config = {
      headers: { Authorization: `Bearer ${token}` }
    };

    useEffect(() => {
      api
        .post("/funcionario/listar", {
          id_estabelecimento: 1
        }, config)
        .then((response) =>  this.setState({funcionarios:response.data}));
    }, []);

    let funcionariosDisponivel: any = [];

    let fun: any = [];
//    console.log(this.state);

    fun.forEach((element: any) => {
      funcionariosDisponivel.push(
        <CardFuncionario>
          <button value='gracione' name='tes'>
            <h5>{element.nome_usuario}</h5>
            <h6>{element.funcao}</h6>
            <h2>{element.id_funcao}</h2>
          </button>
        </CardFuncionario>
      )
    });

    return funcionariosDisponivel;
  }

  handleChange(event: any) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event: any) {
    alert('Um nome foi enviado: '/* + this.state.value*/);
    event.preventDefault();
  }

  render() {

    return (
      <Container>
        <Menu></Menu>
        <Conteudo>
          <div className="agendar-horario" >
            <AgendarHorario>
              <label>Agendar Hor√°rio</label>
              <input type="text" value='oiii' name="cliente" placeholder="Digite o nome do cliente" />
              <div className="funcionarios">
                {this.funcionarios()}
              </div>
                <CardAdicionarFuncionario >
                  <h5>Cadastrar</h5>
                  <h6>Funcion√°rio</h6>
                  <h2>+</h2>
                </CardAdicionarFuncionario>
            </AgendarHorario>
            <CompartilheLink>
              <label>Compartilhe o link com seus clientes</label>
              <form action="">
                <input type="text" />
                <button>x</button>
              </form>
            </CompartilheLink>
          </div>
          <HorariosMarcados>
            <label>Hor√°rios Marcados</label>
            <div></div>
          </HorariosMarcados>
        </Conteudo>
      </Container >
    );
  }
}
